import React from 'react';
import { 
  Paper, 
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Box
} from '@mui/material';

function LaunchGrid({ launches, selectedLaunch, onSelectLaunch }) {
  // Sort launches by date (newest first)
  const sortedLaunches = [...launches].sort((a, b) => 
    new Date(b.date_utc) - new Date(a.date_utc)
  );

  return (
    <Paper sx={{ height: '100vh', overflow: 'auto', p: 2, '&::-webkit-scrollbar': { width: '8px' }, '&::-webkit-scrollbar-thumb': { backgroundColor: 'rgba(0,0,0,0.2)', borderRadius: '4px' } }}>
      <Grid container spacing={2}>
        {sortedLaunches.map((launch) => (
          <Grid item xs={12} sm={6} key={launch.id}>
            <Card 
              sx={{ 
                cursor: 'pointer',
                border: selectedLaunch?.id === launch.id ? 2 : 0,
                borderColor: 'primary.main',
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                transition: 'all 0.3s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: (theme) => `0 8px 24px ${theme.palette.primary.main}40`
                },
                background: 'linear-gradient(145deg, rgba(26, 44, 78, 0.6) 0%, rgba(10, 25, 47, 0.8) 100%)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255, 255, 255, 0.1)'
              }}
              onClick={() => onSelectLaunch(launch)}
              elevation={selectedLaunch?.id === launch.id ? 8 : 2}
            >
              <CardMedia
                component="img"
                height="200"
                image={launch.links?.patch?.small || launch.links?.flickr?.small?.[0] || 'https://via.placeholder.com/200?text=ðŸš€'}
                alt={launch.name}
                sx={{ objectFit: 'contain', p: 2, bgcolor: 'grey.100' }}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom noWrap>
                  {launch.name}
                </Typography>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Typography variant="body2" color="text.secondary">
                    {new Date(launch.date_utc).toLocaleDateString()}
                  </Typography>
                  <Typography 
                    variant="body2" 
                    color={launch.success ? 'success.main' : 'error.main'}
                    fontWeight="bold"
                  >
                    {launch.success ? 'Success' : 'Failed'}
                  </Typography>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
}

export default LaunchGrid;
