import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Chip,
  Divider,
  Paper,
  Grid 
} from '@mui/material';
import { fetchCrewMember } from '../services/spaceXApi';

function LaunchDetail({ launch }) {
  const [crewDetails, setCrewDetails] = useState([]);

  useEffect(() => {
    const fetchCrewDetails = async () => {
      if (launch.crew?.length > 0) {
        const crewPromises = launch.crew.map(async crew => {
          const crewMember = await fetchCrewMember(crew.id);
          if (crewMember) {
            const nasaData = await fetchNASAAstronautData(crewMember.name);
            return { ...crewMember, nasaData };
          }
          return null;
        });
        const crewData = await Promise.all(crewPromises);
        setCrewDetails(crewData.filter(crew => crew !== null));
      }
    };
    fetchCrewDetails();
  }, [launch]);

  return (
    <Box sx={{ 
      p: 3, 
      height: '100vh',
      overflow: 'auto',
      '&::-webkit-scrollbar': {
        width: '8px'
      },
      '&::-webkit-scrollbar-thumb': {
        backgroundColor: 'rgba(255, 255, 255, 0.2)',
        borderRadius: '4px'
      }
    }}>
      <Typography variant="h4" gutterBottom>
        {launch.name}
      </Typography>

      <Chip 
        label={launch.success ? 'Success' : 'Failed'} 
        color={launch.success ? 'success' : 'error'} 
        sx={{ mb: 2 }}
      />

      {launch.links?.webcast && (
        <Box sx={{ mb: 3 }}>
          <Paper sx={{ p: 2, background: 'rgba(26, 44, 78, 0.6)' }}>
            <Typography variant="h6" gutterBottom sx={{ color: '#00f5ff' }}>
              Watch Launch
            </Typography>
            <iframe
              width="100%"
              height="315"
              src={`https://www.youtube.com/embed/${launch.links.webcast.split('v=')[1]}`}
              title="Launch Video"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </Paper>
        </Box>
      )}

